import axios from 'axios'

const Books = () => {
    const [books, setBooks] = useState([]);

    useEffect(()=>{
        const fetchAllBooks = async() => {
            try {
                const res = await axios.get("http://localhost:8800/books");
                setBooks(res.data);
                console.log(res)
            }
            catch (err) {
                console.log(err);
            }
        }
        fetchAllBooks();
    }, [])
    // EL array vacio es para que corra solo nua vez?

  return (
    <div>
        <h1>Librer√≠a de leo</h1>
        <div className="books">
            {books.map(book=>(
                <div className="book">
                    {book.cover && <img src={"book.cover"} />}
                </div>
            ))}
        </div>
    </div>
  )
}

export default Books
